DELIMITER //
DROP PROCEDURE IF EXISTS procs;
CREATE PROCEDURE procs()
BEGIN
  SELECT  routine_schema AS Banque,
          routine_name AS Nom,
          routine_type AS "Type"
  FROM information_schema.routines
  WHERE routine_schema = database()
  ORDER BY routine_name;
END //
DELIMITER ;
